let resumen=document.getElementById("form-resumen"),notification=document.getElementById("form-notification");const valorTicket=parseInt(1e3);function pepe(){let e=parseInt(document.getElementById("form-cant").value),t=parseInt(document.getElementById("form-product").value);resumen=1==t?e*valorTicket*.2+" (80% Off)":2==t?e*valorTicket*.5+" (50% Off)":3==t?e*valorTicket*.85+" (15% Off)":e*valorTicket+" (Sin descuento)",notification.innerHTML=resumen}resumen.addEventListener("click",pepe);